INSERT INTO `articles` (`id`, `public`, `cat`, `rutitle`, `rotitle`, `rucontent`, `rocontent`, `images`) VALUES
(2, 0, 1, 'Сетка шпалерная', 'Сетка шпалерная', 'a', 'a', ''),
(3, 1, 2, 'Шпагат сельскохозяйственный', 'Шпагат сельскохозяйственный', '', '', ''),
(4, 0, 3, 'Нетканый укрывной материал (Лютрасил) производства Германия', 'Нетканый укрывной материал (Лютрасил) производства Германия', '<p>\r\n	Овощеводство в условиях умененного климата находится под угрозой заморозков весной, а также ранним летом и осенью. В последние годы за рубежом и в нашей стране стали использовать укрывные материалы из полимерных волокон. Нетканый материал марки LUTRASIL изготавливают из волокон стабилизированного полипропилена - нейтрального соединения, которое не вступает в реакции, не аккумулирует и не выделяет в окружающую среду никаких вредных веществ, не разрушается (в отличии от полиэтиленовой пленки) под действием ультрафиолетовых лучей. При аккуратном обращении его можно использовать в течении нескольких сезонов. LUTRASIL легкий, эластичный, пропускает до 90% солнечного света, в то же время рассеивает прямые солнечные лучи. Он легко пропускает влагу во время дождя и полива, защищает растения от резких перепадов температуры, небольших заморозков, туманов, сильной жары, августовской холодной росы, а также от вредителей и болезней. Конденсат на внутреней стороне не образуется, т.к. структура материала обеспечивает равномерную циркуляцию воздуха. LUTRASIL (белый, черный) - тонкий, очень легкий (1 кв.м. весит 17 гр.), но вместе с тем прочный и эластичный материал. Приминяется как для непосредственного укрытия растений, так и на невысоких каркасах. Очень эффективно использование LUTRASILа при выращивании раннего картофеля, а в мешочках из него можно проращивать семенные клубни. В наличии есть широкий ассортимент типоразмеров от 3мм до 16,6 мм и длиной 100 м.</p>\r\n', '', '/skins/images/products/arbuz.jpg'),
(5, 1, 4, 'Аксессуары для выращивания тепличных растений', 'Аксессуары для выращивания тепличных растений', '', '', ''),
(7, 1, 6, 'Профессиональные теплицы', 'Профессиональные теплицы', '', '', ''),
(8, 0, 7, 'Кассеты рассадные', 'Кассеты рассадные', '<p>\r\n	Кассеты предназначены для производства высококачественной рассады для закрытого и открытого грунта. Такая рассада позволяет получить урожай в ранние сроки и высокого качества. Кроме того, использование кассет приводит к снижению затрат на семена, т.к. каждое семя высевается в отдельную ячейку и тем самым обеспечивает равномерность стояния растений. Кассеты изготавливаются из толстостенного полистирола и полипропилена. При нормальной эксплуатации кассеты прослужат до 10 лет, что значительно снижает стоимость каждого растения. Большие отверстия и выступы на дне кассет обеспечивают оптимальный дренаж и вентиляцию.</p>\r\n', '', '/skins/images/products'),
(10, 1, 8, 'Плёнка тепличная производство Израиль', 'Producția de folie cu efect de seră din Israel', '<p>\r\n	Плёнка тепличная производство Израиль</p>\r\n', '<p>\r\n	Producția de folie cu efect de seră din Israel</p>\r\n', '/skins/images/products'),
(11, 1, 9, 'Торф', 'Торф', 'Торф Статья', 'Торф Статья', '');
INSERT INTO `fertilizers` (`id`, `public`, `rutitle`, `rotitle`, `rucontent`, `rocontent`) VALUES
(1, 1, 'NPK', 'NPK', '<p>\r\n	GROGREEN NPK диапазон подходит для широкого спектра применений, почвенных условий и культур. Концепция GROGREEN удобрений NPK является предоставление полного питания растений в сумке.<br />\r\n	<br />\r\n	<a href="http://farmer.loc/ru/fertilizers"><strong>SOLUTION</strong></a><br />\r\n	<a href="http://farmer.loc/ru/fertilizers"><strong>SNOW</strong></a><br />\r\n	<a href="http://farmer.loc/ru/fertilizers"><strong>SUBTIL</strong></a><br />\r\n	<a href="http://farmer.loc/ru/fertilizers"><strong>GRINTA</strong></a><br />\r\n	<a href="http://farmer.loc/ru/fertilizers"><strong>GL</strong></a></p>\r\n', '<p>\r\n	GROGREEN NPK диапазон подходит для широкого спектра применений, почвенных условий и культур. Концепция GROGREEN удобрений NPK является предоставление полного питания растений в сумке.<br />\r\n	<br />\r\n	<a href="http://farmer.loc/ru/fertilizers"><strong>SOLUTION</strong></a><br />\r\n	<a href="http://farmer.loc/ru/fertilizers"><strong>SNOW</strong></a><br />\r\n	<a href="http://farmer.loc/ru/fertilizers"><strong>SUBTIL</strong></a><br />\r\n	<a href="http://farmer.loc/ru/fertilizers"><strong>GRINTA</strong></a><br />\r\n	<a href="http://farmer.loc/ru/fertilizers"><strong>GL</strong></a></p>\r\n'),
