INSERT INTO `fertilizers_content` (`id`, `public`, `category`, `rutitle`, `rotitle`, `rucontent`, `rocontent`) VALUES
(1, 1, '1', 'Минеральные удобрения', 'Минеральные удобрения', '<p>\r\n	Высокие темпы развития, возросшие объемы производства привели руководство &laquo;Норск Гидро&raquo; к решению о выделении аграрного сектора в самостоятельную структуру. В этой связи 25 марта 2004 года - с момента регистрации новой компании &laquo;Яра&raquo; (Yara) на фондовой бирже в г. Осло - открылась новая веха для крупнейшего в мире производителя и поставщика широкого спектра минеральных удобрений.<br />\r\n	<br />\r\n	Новое название - &laquo;Яра&raquo; (Yara) - символизирует землю, почву, урожай и благополучие. Сохранился и логотип - корабль викингов - как знак качества продукции, проверенной в течение десятилетий.<br />\r\n	В настоящее время &laquo;Яра&raquo; (Yara) продолжает развиваться, решительно продвигаясь вперед, оставляя конкурентов за бортом. Столетний опыт работы, огромный научно-производственный потенциал позволяют производить продукцию, отвечающую самым строгим требованиям современности, и обеспечивать стабильность &laquo;кораблю викингов&raquo;.<br />\r\n	Ежегодный объем производимых удобрений (20 млн.т) поставляется в более чем 120 стран мира. В 50 представительствах компании по всему миру работают 7500 человек.<br />\r\n	<br />\r\n	В России и в других странах СНГ компания получила известность и признание благодаря высокому качеству удобрений и простоте технологий их применения. За относительно короткий промежуток времени - с 1997 по 2004 год - удалось завоевать и упрочить позиции в основном сегменте рынка защищенного грунта (тепличный сектор). Это крупные тепличные комбинаты: ЗАО &laquo;Агрокомбинат &laquo;Московский&raquo; (Московская обл.), САОЗТ &laquo;Лето&raquo; (Санкт-Петербург), ГУЛ &laquo;Южный&raquo; (г. Усть-Джегута). В 2003 году в краснодарском агрокомбинате &laquo;Тепличный&raquo; впервые в СНГ получен рекордный урожай томатов - до 50 кг с одного квадратного метра. Это стало возможным благодаря четкому соблюдению технологии минерального питания с применением удобрений &laquo;Яра&raquo; (Yara): кальциевой селитры, ряда кристалонов, микроэлементов в хелатной форме и др.<br />\r\n	<br />\r\n	В 2001 году на основе глубокого научного исследования и широкой производственной практики компания совместно с ведущими НИИ России и Украины разработала и внедрила в сельхозпроизводство новейшие схемы дополнительного минерального питания для зерновых, технических, овощных других культур. Данные рекомендации гармонично усиливают традиционные технологии выращивания самого широкого спектра растений.<br />\r\n	<br />\r\n	На сегодняшний день на территории СНГ водорастворимые удобрения марки <strong>Кристалон</strong> с набором микроэлементов применяются на более чем 2,5 млн. га, и эти площади постоянно растут. Завод по производству кристалонов в Голландии расширяет производственные мощности, учитывая возросшие потребности рынка России и других стран СНГ.<br />\r\n	<br />\r\n	Специалисты единодушны в том, что <strong>Кристалон</strong> до сих пор не имеет аналогов по химической чистоте и стабильности всех компонентов, отсутствию балластов и вредных примесей, физиологически выверенным концентрациям макро- и микроэлементов в хелатной форме. Небольшие дозировки, полная растворимость и возможность совмещения с другими химическими обработками обеспечивают технологические удобства применения этого уникального удобрения. Невысокие затраты (3-5 долл. на 1 га) при существенной прибыли (более 70 долл. с 1 га) и высоком качестве делают Кристалон доступным и привлекательным как для мелких фермерских хозяйств, так и для крупных сельхозтоваропроизводителей.<br />\r\n	<br />\r\n	Следуя международной практике организации продаж и в целях эффективного и своевременного обеспечения наших покупателей продукцией компании &quot;Яра&quot; (Yara) на территории СНГ в 2004 году была создана компания &quot;Нутритек Систем Инк.&quot; во главе с Джонни Стремволлом.<br />\r\n	<br />\r\n	&quot;Нутритек Систем Инк.&quot; (Nutritech System Inc.) является основным и единственным поставщиком всего спектра удобрений в Россию и другие страны СНГ.</p>\r\n', '<p>\r\n	Высокие темпы развития, возросшие объемы производства привели руководство &laquo;Норск Гидро&raquo; к решению о выделении аграрного сектора в самостоятельную структуру. В этой связи 25 марта 2004 года - с момента регистрации новой компании &laquo;Яра&raquo; (Yara) на фондовой бирже в г. Осло - открылась новая веха для крупнейшего в мире производителя и поставщика широкого спектра минеральных удобрений.<br />\r\n	<br />\r\n	Новое название - &laquo;Яра&raquo; (Yara) - символизирует землю, почву, урожай и благополучие. Сохранился и логотип - корабль викингов - как знак качества продукции, проверенной в течение десятилетий.<br />\r\n	В настоящее время &laquo;Яра&raquo; (Yara) продолжает развиваться, решительно продвигаясь вперед, оставляя конкурентов за бортом. Столетний опыт работы, огромный научно-производственный потенциал позволяют производить продукцию, отвечающую самым строгим требованиям современности, и обеспечивать стабильность &laquo;кораблю викингов&raquo;.<br />\r\n	Ежегодный объем производимых удобрений (20 млн.т) поставляется в более чем 120 стран мира. В 50 представительствах компании по всему миру работают 7500 человек.<br />\r\n	<br />\r\n	В России и в других странах СНГ компания получила известность и признание благодаря высокому качеству удобрений и простоте технологий их применения. За относительно короткий промежуток времени - с 1997 по 2004 год - удалось завоевать и упрочить позиции в основном сегменте рынка защищенного грунта (тепличный сектор). Это крупные тепличные комбинаты: ЗАО &laquo;Агрокомбинат &laquo;Московский&raquo; (Московская обл.), САОЗТ &laquo;Лето&raquo; (Санкт-Петербург), ГУЛ &laquo;Южный&raquo; (г. Усть-Джегута). В 2003 году в краснодарском агрокомбинате &laquo;Тепличный&raquo; впервые в СНГ получен рекордный урожай томатов - до 50 кг с одного квадратного метра. Это стало возможным благодаря четкому соблюдению технологии минерального питания с применением удобрений &laquo;Яра&raquo; (Yara): кальциевой селитры, ряда кристалонов, микроэлементов в хелатной форме и др.<br />\r\n	<br />\r\n	В 2001 году на основе глубокого научного исследования и широкой производственной практики компания совместно с ведущими НИИ России и Украины разработала и внедрила в сельхозпроизводство новейшие схемы дополнительного минерального питания для зерновых, технических, овощных других культур. Данные рекомендации гармонично усиливают традиционные технологии выращивания самого широкого спектра растений.<br />\r\n	<br />\r\n	На сегодняшний день на территории СНГ водорастворимые удобрения марки <strong>Кристалон</strong> с набором микроэлементов применяются на более чем 2,5 млн. га, и эти площади постоянно растут. Завод по производству кристалонов в Голландии расширяет производственные мощности, учитывая возросшие потребности рынка России и других стран СНГ.<br />\r\n	<br />\r\n	Специалисты единодушны в том, что <strong>Кристалон</strong> до сих пор не имеет аналогов по химической чистоте и стабильности всех компонентов, отсутствию балластов и вредных примесей, физиологически выверенным концентрациям макро- и микроэлементов в хелатной форме. Небольшие дозировки, полная растворимость и возможность совмещения с другими химическими обработками обеспечивают технологические удобства применения этого уникального удобрения. Невысокие затраты (3-5 долл. на 1 га) при существенной прибыли (более 70 долл. с 1 га) и высоком качестве делают Кристалон доступным и привлекательным как для мелких фермерских хозяйств, так и для крупных сельхозтоваропроизводителей.<br />\r\n	<br />\r\n	Следуя международной практике организации продаж и в целях эффективного и своевременного обеспечения наших покупателей продукцией компании &quot;Яра&quot; (Yara) на территории СНГ в 2004 году была создана компания &quot;Нутритек Систем Инк.&quot; во главе с Джонни Стремволлом.<br />\r\n	<br />\r\n	&quot;Нутритек Систем Инк.&quot; (Nutritech System Inc.) является основным и единственным поставщиком всего спектра удобрений в Россию и другие страны СНГ.</p>\r\n');
INSERT INTO `fertilizers_content` (`id`, `public`, `category`, `rutitle`, `rotitle`, `rucontent`, `rocontent`) VALUES
(2, 1, '1', 'Организация эффективного минерального питания сельскохозяйственных культур', 'Организация эффективного минерального питания сельскохозяйственных культур', '<div align="justify">\r\n	<p>\r\n		<em>Без организации эффективного минерального питания выращивание сельскохозяйственных культур низкорентабельно, теряют смысл затраты на семена, пестициды и комплекс полевых и уборочных работ.</em><br />\r\n		<br />\r\n		Особое значение, именно в эффективности питания, имеют микроэлементы - бор, молибден, медь, цинк, железо, марганец. Ни одно растение не может нормально развиваться без этих элементов, так как они входят в состав важнейших ферментов, витаминов, гормонов и других физиологически активных соединений, играющих большую роль в жизни растений. Микроэлементы участвуют в процессах синтеза белков, углеводов, жиров, витаминов. Под их влиянием увеличивается содержание хлорофилла в листьях, усиливается ассимилирующая деятельность всего растения, улучшается процесс фотосинтеза. Исключительно важную роль играют микроэлементы в процессах оплодотворения. Они положительно влияют на развитие семян и их посевные качества. Под их воздействием растения становятся более устойчивыми к неблагоприятным условиям выращивания, к засухе, поражению болезнями, вредителями и т.д.<br />\r\n		<br />\r\n		Компания &laquo;ЯРА&raquo; (Гидро Агри) - одно из подразделений Норвежского государственного концерна Норск Гидро - около ста лет назад первой в мире начала промышленное производство минеральных удобрений. Это лидерство сохранено до настоящего времени как в качестве, так и в количестве (12-15 млн. тонн в год). Достижения мировой агрохимии и столетний опыт воплотились в поистине уникальные формы и виды (около 600) минеральных удобрений для различных культур и условий выращивания, имеющие в своем составе все необходимые и легко доступные растениям макро- и микроэлементы (в хелатной форме) в сбалансированном соотношении. Особые формулы минеральных удобрений &laquo;ЯРА&raquo; (Гидро), химическая чистота, отсутствие вредных соединений, удобная расфасовка и Доступные цены обуславливают широкий спектр их применения - от комнатных Растений до полевых культур и декоративных насаждений.<br />\r\n		<br />\r\n		Использование удобрений &laquo;ЯРА&raquo; (Гидро Агри) по специально разработанным технологиям (для любых сельскохозяйственных культур) позволяют получать максимальный эффект при незначительных затратах. Растения находятся в многосторонней и тесной связи с окружающей внешней средой. При благоприятном сочетании всех факторов жизни получают максимальную продуктивность растений и качество урожая. Недостаток одного из условий жизни растения угнетает его развитие, а отсутствие приводит к гибели. В практике земледелия чаще приходится сталкиваться с недостатком питательных веществ и воды.<br />\r\n		<br />\r\n		Практически любой стресс-фактор приводит к нарушению питания растений - засуха, низкая или высокая температура и влажность почвы или воздуха, уплотненность почвы и плохая аэрация, низкий или высокий показатель pH, высокое содержание ионов антагонистов и органических веществ. То есть даже при достаточном количестве элементов питания в почве растения не всегда в состоянии использовать их в полной мере. Нарушение питания - это прямые потери урожая и качества.</p>\r\n</div>\r\n<div align="center" class="maintext-table-head">\r\n	Основное влияние элементов минерального питания на процессы,<br />\r\n	определяющие урожаи и его качество.<br />\r\n	<br />\r\n	Макроэлементы<br />\r\n	(их вынос с урожаем исчисляется в кг на 1 т продукции)</div>\r\n\r\n<table align="center" border="1" cellpadding="0" cellspacing="0" class="maintext-table-text" width="98%">\r\n	<tbody>\r\n		<tr bgcolor="#E1E1E1" valign="top">\r\n			<td bgcolor="#E1E1E1" valign="top" width="98">\r\n				<div align="center">\r\n					<strong>Азот</strong></div>\r\n			</td>\r\n			<td valign="top" width="136">\r\n				<div align="center">\r\n					<strong>Фосфор</strong></div>\r\n			</td>\r\n			<td valign="top" width="134">\r\n				<div align="center">\r\n					<strong>Калий</strong></div>\r\n			</td>\r\n			<td valign="top" width="111">\r\n				<div align="center">\r\n					<strong>Магний</strong></div>\r\n			</td>\r\n			<td valign="top" width="97">\r\n				<div align="center">\r\n					<strong>Кальций</strong></div>\r\n			</td>\r\n		</tr>\r\n		<tr valign="top">\r\n			<td bgcolor="#F2F2F2" valign="top" width="20%">\r\n				Элемент образования органического вещества.<br />\r\n				Регулирует рост вегетативной массы. Определяет уровень урожайности</td>\r\n			<td valign="top" width="20%">\r\n				Элемент энергетического обеспечения. Активирует рост корневой системы и закладки генеративных органов. Ускоряет развитие всех процессов. Повышает зимостойкость</td>\r\n			<td bgcolor="#F2F2F2" valign="top" width="20%">\r\n				Элемент молодости клеток. Усиливает образование сахаров и их передвижение по тканям. Повышает устойчивость к болезням, засухе и заморозкам.</td>\r\n			<td valign="top" width="20%">\r\n				Повышает интенсивность фотосинтеза и образование хлорофилла. Влияет на окислительно-восстановительные процессы. Активирует ферментативные про цессы.</td>\r\n			<td bgcolor="#F2F2F2" valign="top" width="20%">\r\n				Стимулирует рост растения и развитие корневой системы. Усиливает обмен веществ, активирует ферменты.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	<br />\r\n	<div align="center" class="maintext-table-head">\r\n		Макроэлементы<br />\r\n		(их вынос с урожаем исчисляется в кг на 1 т продукции)</div>\r\n</p>\r\n<table align="center" border="1" cellpadding="0" cellspacing="0" class="maintext-table-text" width="98%">\r\n	<tbody>\r\n		<tr bgcolor="#E1E1E1" valign="top">\r\n			<td valign="top" width="91">\r\n				<div align="center">\r\n					<strong>Железо</strong></div>\r\n			</td>\r\n			<td valign="top" width="99">\r\n				<div align="center">\r\n					<strong>Медь</strong></div>\r\n			</td>\r\n			<td valign="top" width="104">\r\n				<div align="center">\r\n					<strong>Марганец</strong></div>\r\n			</td>\r\n			<td valign="top" width="106">\r\n				<div align="center">\r\n					<strong>Цинк</strong></div>\r\n			</td>\r\n			<td valign="top" width="91">\r\n				<div align="center">\r\n					<strong>Бор</strong></div>\r\n			</td>\r\n			<td valign="top" width="86">\r\n				<div align="center">\r\n					<strong>Молибден</strong></div>\r\n			</td>\r\n		</tr>\r\n		<tr valign="top">\r\n			<td bgcolor="#F2F2F2" valign="top" width="16%">\r\n				Регулирует фотосинтез, дыхание, белковый обмен и биосинтез ростовых веществ - ауксинов.</td>\r\n			<td valign="top" width="16%">\r\n				Регулирует дыхание, фотосинтез, углеводный и белковый обмен.<br />\r\n				Повышает засухо-, морозо- и жароустойчивость.</td>\r\n			<td bgcolor="#F2F2F2" valign="top" width="16%">\r\n				Регулирует фотосинтез, дыхание, углеводный и белковый обмен.<br />\r\n				Входит в состав и активирует ферменты.</td>\r\n			<td valign="top" width="16%">\r\n				Регулирует белковый, липоидный, углеводный, фосфорный обмен и биосинтез витаминов и ростовых веществ - ауксинов.</td>\r\n			<td bgcolor="#F2F2F2" valign="top" width="16%">\r\n				Регулирует опыление и оплодотворение, углеводный и белковый обмен.<br />\r\n				Повышает устойчивость к болезням.</td>\r\n			<td valign="top" width="16%">\r\n				Регулирует азотный, углеводный и фосфорный обмен, синтез хлорофилла и витаминов, стимулирует фиксацию азота воздуха.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	<br />\r\n	<div align="justify" class="maintext">\r\n		Все элементы минерального питания тесно связаны между собой участием в единых процессах, но роль каждого из них строго специфична. Это сформулировано в законе Либиха (закон минимума): определяющим урожаи и его качество является элемент, находящийся в минимуме, независимо от того, в каком количестве он требуется растению. Поэтому роль микроэлементов в получении высоких и полноценных урожаев сельскохозяйственных культур столь же велика и не менее значима, сколь и основных элементов минерального питания - азота, фосфора, калия, кальция и магния.</div>\r\n</p>\r\n<div align="center" class="maintext-table-head">\r\n	Факторы, снижающие подвижность и усвоение элементов<br />\r\n	минерального питания растениями (А.В. Чумаков)</div>\r\n<table align="center" border="1" cellpadding="0" cellspacing="0" class="maintext-table-text" width="98%">\r\n	<tbody>\r\n		<tr bgcolor="#E1E1E1" valign="top">\r\n			<td bgcolor="#E1E1E1" valign="top">\r\n				<div align="center">\r\n					<strong>Азот</strong></div>\r\n			</td>\r\n			<td valign="top">\r\n				<div align="center">\r\n					<strong>Фосфор</strong></div>\r\n			</td>\r\n			<td valign="top">\r\n				<div align="center">\r\n					<strong>Калий</strong></div>\r\n			</td>\r\n			<td valign="top">\r\n				<div align="center">\r\n					<strong>Магний</strong></div>\r\n			</td>\r\n			<td valign="top">\r\n				<div align="center">\r\n					<strong>Кальций</strong></div>\r\n			</td>\r\n			<td valign="top">\r\n				<div align="center">\r\n					<strong>Сера</strong></div>\r\n			</td>\r\n		</tr>\r\n		<tr valign="top">\r\n			<td bgcolor="#F2F2F2" valign="top" width="16%">\r\n				Холодная погода, уплотненная и холодная почва, слабая микробиологическая деятельность, запахивание большого количества соломы, недостаток влаги.</td>\r\n			<td valign="top" width="16%">\r\n				Низкая температура почвы и воздуха, избыток ионов Al, Fe, Mn, хлориди нитрат-ионов в почве, низкие значения рН.</td>\r\n			<td bgcolor="#F2F2F2" valign="top" width="16%">\r\n				Теплая и сухая погода, высокое содержание ионов Са и Мg в почве</td>\r\n			<td valign="top" width="16%">\r\n				Высокие дозы удобрений, содержащих ионы K,Na, NH4</td>\r\n			<td bgcolor="#F2F2F2" valign="top" width="16%">\r\n				Сухая и теплая погода, колебание влажности почвы, изобилие NH4- ИОНОВ, калийных и магниевых удобрении.</td>\r\n			<td valign="top" width="16%">\r\n				Избыточные дозы фосфорных и азотных удобрений, высокая концентрация селена в почве, низкая температура.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	<br />\r\n	<table align="center" border="1" cellpadding="0" cellspacing="0" class="maintext-table-text" width="98%">\r\n		<tbody>\r\n			<tr bgcolor="#E1E1E1" valign="top">\r\n				<td bgcolor="#E1E1E1" valign="top" width="58">\r\n					<div align="center">\r\n						<strong>Железо</strong></div>\r\n				</td>\r\n				<td bgcolor="#E1E1E1" valign="top" width="51">\r\n					<div align="center">\r\n						<strong>Марганец</strong></div>\r\n				</td>\r\n				<td valign="top" width="50">\r\n					<div align="center">\r\n						<strong>Цинк</strong></div>\r\n				</td>\r\n				<td valign="top" width="49">\r\n					<div align="center">\r\n						<strong>Медь</strong></div>\r\n				</td>\r\n				<td valign="top" width="38">\r\n					<div align="center">\r\n						<strong>Бор</strong></div>\r\n				</td>\r\n				<td valign="top" width="49">\r\n					<div align="center">\r\n						<strong>Молибден</strong></div>\r\n				</td>\r\n			</tr>\r\n			<tr valign="top">\r\n				<td bgcolor="#F2F2F2" valign="top" width="16%">\r\n					Высокая влажность или переув- лажнение почвы, обилие Р и недостаток К в почве. низкая или высокая температура. избыток растворимых солей тяжелых металлов в кислых почвах, плохая аэрация, высокое содержание органического вещества</td>\r\n				<td valign="top" width="16%">\r\n					Сухая погода, низкая температура почвы, низкая интенсивность освещения, высокое содержание ионов P. Fe, Cu, Zn в почве, высокое содержание органического вещества</td>\r\n				<td bgcolor="#F2F2F2" valign="top" width="16%">\r\n					Высокие дозы фосфорных и азотных удобрений, обильное известкование, низкая температура, уплотненная почва, низкое содержание органического вещества.</td>\r\n				<td valign="top" width="16%">\r\n					Высокая концентрация ионов Р. N и Zn в почве, избыток растворимых соединений тяжелых металлов в почве, жаркая погода, высокое содержание органического вещества.</td>\r\n				<td bgcolor="#F2F2F2" valign="top" width="16%">\r\n					Засуха, избыточная влажность, интенсивное освещение, изобилие азотных и калийных удобрении.</td>\r\n				<td valign="top" width="16%">\r\n					Высокое содержание ионов Mn, Fe и Си и сульфат-ионов в почве, высо кие дозы нитратного азота, высокое содержание органического вещества.</td>\r\n			</tr>\r\n		</tbody>\r\n	</table>\r\n</p>\r\n<p>\r\n	&nbsp;</p>\r\n<div align="center" class="maintext-table-head">\r\n	Оптимальная кислотность почвы для наилучшей доступности растению микроэлементов</div>\r\n<table align="center" border="1" cellpadding="0" cellspacing="0" class="maintext-table-text" width="98%">\r\n	<tbody>\r\n		<tr valign="top">\r\n			<td bgcolor="#E1E1E1" valign="top" width="27">\r\n				&nbsp;</td>\r\n			<td bgcolor="#E1E1E1" valign="top" width="45">\r\n				<div align="center">\r\n					<strong>Бор</strong></div>\r\n			</td>\r\n			<td bgcolor="#E1E1E1" valign="top" width="45">\r\n				<div align="center">\r\n					<strong>Медь</strong></div>\r\n			</td>\r\n			<td bgcolor="#E1E1E1" valign="top" width="44">\r\n				<div align="center">\r\n					<strong>Железо</strong></div>\r\n			</td>\r\n			<td bgcolor="#E1E1E1" valign="top" width="44">\r\n				<div align="center">\r\n					<strong>Марганец</strong></div>\r\n			</td>\r\n			<td bgcolor="#E1E1E1" valign="top" width="42">\r\n				<div align="center">\r\n					<strong>Молибден</strong></div>\r\n			</td>\r\n			<td bgcolor="#E1E1E1" valign="top" width="30">\r\n				<div align="center">\r\n					<strong>Цинк</strong></div>\r\n			</td>\r\n		</tr>\r\n		<tr valign="top">\r\n			<td valign="top" width="14%">\r\n				РН почвы</td>\r\n			<td bgcolor="#F2F2F2" valign="top" width="14%">\r\n				5.0-7.0</td>\r\n			<td valign="top" width="14%">\r\n				5.0-7.0</td>\r\n			<td bgcolor="#F2F2F2" valign="top" width="14%">\r\n				4.0-6.5</td>\r\n			<td valign="top" width="14%">\r\n				5.0-6.5</td>\r\n			<td bgcolor="#F2F2F2" valign="top" width="14%">\r\n				7.0-8.5</td>\r\n			<td valign="top" width="14%">\r\n				5.0-7.0</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	<br />\r\n	<div align="justify" class="maintext">\r\n		Однако химический анализ почвы на содержание доступных растениям форм микроэлементов, в силу двух основных причин, нельзя считать реально отражающим необходимую потребность растений.<br />\r\n		<br />\r\n		<strong>Первая причина</strong> - Большинство исследователей подразумевают все формы и количество микроэлементов, переходящих в любую вытяжку: водную, солевую, в разбавленные сильные минеральные и слабые органические кислоты, щелочи и другие растворы. При этом часто между подвижными и доступными растениям формами микроэлементов не делают различий. При сопоставлении же размеров потребления микроэлементов растениями с их количеством в почве, извлекаемым агрессивными вытяжками, можно сделать вывод, что растениями используется менее 1 % извлекаемых из почвы микроэлементов. Поэтому следует проявлять известную осторожность при оценке обеспеченности почв усвояемыми формами микроэлементов. (Академик ВАСХНИЛ Б.А. Ягодин.)<br />\r\n		<br />\r\n		<strong>Вторая причина</strong> - Даже на почвах с высоким содержанием микроэлементов растения в силу различных причин могут испытывать голодание от недостатка тех или иных элементов. Фактически любые почвенно-климатические условия могут влиять на подвижность и усвояемость микроэлементов растениями.<br />\r\n		<br />\r\n		Анализ материалов по производству и применению микроудобрений в России показывает, что роль микроудобрений в сельском хозяйстве по меньшей мере недооценивают. Обеспеченность пашни подвижными формами микроэлементов крайне неудовлетворительна. По данным крупномасштабного агрохимического обследования почв, проведенного агрохим-службой еще в середине 80-х годов, во внесении микроудобрений нуждается большинство почв пашни: в борных - 59,5%, цинковых - 83%, мед-ных - 64,5%, молибденовых - 75,3%, марганцевых - 41,3%. (А.Н. Аристархов, А.Н. Поляков и др.)</div>\r\n</p>\r\n<p>\r\n	<br />\r\n	<div align="center" class="maintext-table-head">\r\n		Эффективность применения микроудобрений под<br />\r\n		основные сельскохозяйственные культуры<br />\r\n		(по А.Н. Аристархову, А.Н. Полякову и др.)<br />\r\n		<table border="1" cellpadding="0" cellspacing="0" class="maintext-table-text" width="100%">\r\n			<tbody>\r\n				<tr>\r\n					<td bgcolor="#E1E1E1" rowspan="2" width="28%">\r\n						<div align="center">\r\n							<strong>Культура</strong></div>\r\n					</td>\r\n					<td bgcolor="#E1E1E1" colspan="6" height="35">\r\n						<div align="center">\r\n							<strong>Прибавка урожайности от применения микроэлементов, ц\\га<br />\r\n							(средняя по всем опытам)</strong></div>\r\n					</td>\r\n				</tr>\r\n				<tr>\r\n					<td bgcolor="#E1E1E1" height="20" width="12%">\r\n						<div align="center">\r\n							<strong>B</strong></div>\r\n					</td>\r\n					<td bgcolor="#E1E1E1" height="20" width="12%">\r\n						<div align="center">\r\n							<strong>Mo</strong></div>\r\n					</td>\r\n					<td bgcolor="#E1E1E1" height="20" width="12%">\r\n						<div align="center">\r\n							<strong>Zn</strong></div>\r\n					</td>\r\n					<td bgcolor="#E1E1E1" height="20" width="12%">\r\n						<div align="center">\r\n							<strong>Cu</strong></div>\r\n					</td>\r\n					<td bgcolor="#E1E1E1" height="20" width="12%">\r\n						<div align="center">\r\n							<strong>Co</strong></div>\r\n					</td>\r\n					<td bgcolor="#E1E1E1" height="20" width="12%">\r\n						<div align="center">\r\n							<strong>Mn</strong></div>\r\n					</td>\r\n				</tr>\r\n				<tr>\r\n					<td height="35">\r\n						Сахарная свекла</td>\r\n					<td bgcolor="#F2F2F2" height="35" width="10%">\r\n						<div align="center">\r\n							32,1<br />\r\n							(341)</div>\r\n					</td>\r\n					<td height="35">\r\n						<div align="center">\r\n							22,7<br />\r\n							(203)</div>\r\n					</td>\r\n					<td bgcolor="#F2F2F2" height="35">\r\n						<div align="center">\r\n							32,3<br />\r\n							(115)</div>\r\n					</td>\r\n					<td height="35">\r\n						<div align="center">\r\n							13,9<br />\r\n							(275)</div>\r\n					</td>\r\n					<td bgcolor="#F2F2F2" height="35">\r\n						<div align="center">\r\n							29,6<br />\r\n							(116)</div>\r\n					</td>\r\n					<td height="35">\r\n						<div align="center">\r\n							27,6<br />\r\n							(119)</div>\r\n					</td>\r\n				</tr>\r\n				<tr>\r\n					<td height="35">\r\n						Пшеница, ячмень (зерно)</td>\r\n					<td bgcolor="#F2F2F2" height="35">\r\n						<div align="center">\r\n							1.4<br />\r\n							(208)</div>\r\n					</td>\r\n					<td height="35">\r\n						<div align="center">\r\n							2,1<br />\r\n							(241)</div>\r\n					</td>\r\n					<td bgcolor="#F2F2F2" height="35">\r\n						<div align="center">\r\n							2,5<br />\r\n							(236)</div>\r\n					</td>\r\n					<td height="35">\r\n						<div align="center">\r\n							3,7<br />\r\n							(184)</div>\r\n					</td>\r\n					<td bgcolor="#F2F2F2" height="35">\r\n						<div align="center">\r\n							2,7<br />\r\n							(112)</div>\r\n					</td>\r\n					<td height="35">\r\n						<div align="center">\r\n							1,9<br />\r\n							(215)</div>\r\n					</td>\r\n				</tr>\r\n				<tr>\r\n					<td height="35">\r\n						Картофель</td>\r\n					<td bgcolor="#F2F2F2" height="35">\r\n						<div align="center">\r\n							20,1<br />\r\n							(71)</div>\r\n					</td>\r\n					<td height="35">\r\n						<div align="center">\r\n							20,2<br />\r\n							(105)</div>\r\n					</td>\r\n					<td bgcolor="#F2F2F2" height="35">\r\n						<div align="center">\r\n							23,8<br />\r\n							(54)</div>\r\n					</td>\r\n					<td height="35">\r\n						<div align="center">\r\n							12,7<br />\r\n							(68)</div>\r\n					</td>\r\n					<td bgcolor="#F2F2F2" height="35">\r\n						<div align="center">\r\n							17,9<br />\r\n							(109)</div>\r\n					</td>\r\n					<td height="35">\r\n						<div align="center">\r\n							27,7<br />\r\n							(109)</div>\r\n					</td>\r\n				</tr>\r\n				<tr>\r\n					<td height="35">\r\n						Горох</td>\r\n					<td bgcolor="#F2F2F2" height="35">\r\n						<div align="center">\r\n							2,8<br />\r\n							(75)</div>\r\n					</td>\r\n					<td height="35">\r\n						<div align="center">\r\n							2,7 (155)</div>\r\n					</td>\r\n					<td bgcolor="#F2F2F2" height="35">\r\n						<div align="center">\r\n							3,0 (62)</div>\r\n					</td>\r\n					<td height="35">\r\n						<div align="center">\r\n							3,0<br />\r\n							(62)</div>\r\n					</td>\r\n					<td bgcolor="#F2F2F2" height="35">\r\n						<div align="center">\r\n							2,7 (3)</div>\r\n					</td>\r\n					<td height="35">\r\n						<div align="center">\r\n							-</div>\r\n					</td>\r\n				</tr>\r\n				<tr>\r\n					<td valign="top">\r\n						<p>\r\n							Кукуруза</p>\r\n						<blockquote>\r\n							<p>\r\n								- зерно<br />\r\n								<br />\r\n								<br />\r\n								- зеленая масса</p>\r\n						</blockquote>\r\n					</td>\r\n					<td bgcolor="#F2F2F2" height="20">\r\n						<div align="center">\r\n							<br />\r\n							-<br />\r\n							-<br />\r\n							<br />\r\n							50,7<br />\r\n							(66)</div>\r\n					</td>\r\n					<td height="20">\r\n						<div align="center">\r\n							<br />\r\n							1.3<br />\r\n							(18)<br />\r\n							<br />\r\n							49,2<br />\r\n							(88)</div>\r\n					</td>\r\n					<td bgcolor="#F2F2F2" height="20">\r\n						<div align="center">\r\n							<br />\r\n							5,2<br />\r\n							(220)<br />\r\n							<br />\r\n							43,8<br />\r\n							(88)</div>\r\n					</td>\r\n					<td height="20">\r\n						<div align="center">\r\n							<br />\r\n							-<br />\r\n							-<br />\r\n							<br />\r\n							50,1<br />\r\n							(74)</div>\r\n					</td>\r\n					<td bgcolor="#F2F2F2" height="20">\r\n						<div align="center">\r\n							<br />\r\n							-<br />\r\n							-<br />\r\n							<br />\r\n							40,0<br />\r\n							(54)</div>\r\n					</td>\r\n					<td height="20">\r\n						<div align="center">\r\n							<br />\r\n							2,8<br />\r\n							(51)<br />\r\n							<br />\r\n							38,5<br />\r\n							(62)</div>\r\n					</td>\r\n				</tr>\r\n				<tr>\r\n					<td height="35">\r\n						Соя (зерно)</td>\r\n					<td bgcolor="#F2F2F2" height="35">\r\n						<div align="center">\r\n							1,3<br />\r\n							(7)</div>\r\n					</td>\r\n					<td height="35">\r\n						<div align="center">\r\n							1,7<br />\r\n							(52)</div>\r\n					</td>\r\n					<td bgcolor="#F2F2F2" height="35">\r\n						<div align="center">\r\n							1,4<br />\r\n							(12)</div>\r\n					</td>\r\n					<td height="35">\r\n						<div align="center">\r\n							-<br />\r\n							-</div>\r\n					</td>\r\n					<td bgcolor="#F2F2F2" height="35">\r\n						<div align="center">\r\n							-<br />\r\n							-</div>\r\n					</td>\r\n					<td height="35">\r\n						<div align="center">\r\n							-<br />\r\n							-</div>\r\n					</td>\r\n				</tr>\r\n				<tr>\r\n					<td height="35">\r\n						Рис</td>\r\n					<td bgcolor="#F2F2F2" height="35">\r\n						<div align="center">\r\n							3,2<br />\r\n							(11)</div>\r\n					</td>\r\n					<td height="35">\r\n						<div align="center">\r\n							-<br />\r\n							-</div>\r\n					</td>\r\n					<td bgcolor="#F2F2F2" height="35">\r\n						<div align="center">\r\n							4,2<br />\r\n							(9)</div>\r\n					</td>\r\n					<td height="35">\r\n						<div align="center">\r\n							4,8<br />\r\n							(47)</div>\r\n					</td>\r\n					<td bgcolor="#F2F2F2" height="35">\r\n						<div align="center">\r\n							-<br />\r\n							-</div>\r\n					</td>\r\n					<td height="35">\r\n						<div align="center">\r\n							3,2<br />\r\n							(7)</div>\r\n					</td>\r\n				</tr>\r\n			</tbody>\r\n		</table>\r\n	</div>\r\n</p>\r\n<div align="left" class="maintext">\r\n	<strong>Примечание:</strong> в скобках указано число опытов.</div>\r\n<p>\r\n	<br />\r\n	<div align="justify" class="maintext">\r\n		В настоящее время можно говорить лишь о многократном ухудшении ситуации, что заметно сказывается на качественных и количественных показателях урожая всех сельскохозяйственных культур. Многочисленные опыты по определению влияния микроэлементов на урожай, проведенные до 90-х годов, свидетельствуют об актуальности данной проблемы.<br />\r\n		<br />\r\n		Весьма актуален вопрос о способах применения микроудобрений. Так как они необходимы в небольших количествах - при этом различных по каждому элементу в отдельности - то равномерное внесение их в почву в виде сухой смеси практически невозможно. Кроме того, диапазон оптимальных доз очень узок, и в случае превышения допустимой дозировки может быть получен отрицательный эффект. Важный момент - химическая форма. Так, попытки приготовления смесей неорганических сульфатных солей микроэлементов (Zn, Cu, Fe, Mn) приводили к антагонизму и конкуренции этих элементов в растворе, что в конечном итоге давало отрицательный результат. Кроме того, неорганические соли этих металлов разрушающе действовали на органические структуры пестицидов, что делало невозможным совмещение обработок.</div>\r\n</p>\r\n<p>\r\n	<br />\r\n	<div align="center" class="maintext-table-head">\r\n		Содержание питательных веществ в убранном урожае<br />\r\n		(макроэлементы - кг, микроэлементы - г)</div>\r\n</p>\r\n<table align="center" border="1" cellpadding="0" cellspacing="0" class="maintext-table-text" width="100%">\r\n	<tbody>\r\n		<tr>\r\n			<td bgcolor="#E1E1E1" rowspan="2" valign="top" width="21%">\r\n				<div align="center">\r\n					Элементы минерального<br />\r\n					питания</div>\r\n			</td>\r\n			<td bgcolor="#E1E1E1" colspan="2" height="20">\r\n				<div align="center">\r\n					Средний вынос элементов минерального питания из почвы с урожаем</div>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td bgcolor="#E1E1E1" height="35">\r\n				<div align="center">\r\n					Озимая пшеница (зерно, солома) урожай зерна 40 ц/га</div>\r\n			</td>\r\n			<td bgcolor="#E1E1E1" height="20">\r\n				<div align="center">\r\n					Сахарная свекла (корни, ботва) урожай корней 400 ц/га</div>\r\n			</td>\r\n		</tr>\r\n		<tr bgcolor="#F2F2F2">\r\n			<td>\r\n				&nbsp;</td>\r\n			<td colspan="2">\r\n				<div align="center">\r\n					Макроэлементы ( кг/га )</div>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td bgcolor="#F2F2F2">\r\n				<div align="center">\r\n					N<br />\r\n					<br />\r\n					Р2О5<br />\r\n					<br />\r\n					К2О<br />\r\n					<br />\r\n					MgO<br />\r\n					<br />\r\n					СаО</div>\r\n			</td>\r\n			<td width="38%">\r\n				<div align="center">\r\n					125<br />\r\n					<br />\r\n					54<br />\r\n					<br />\r\n					143<br />\r\n					<br />\r\n					24<br />\r\n					<br />\r\n					39</div>\r\n			</td>\r\n			<td width="41%">\r\n				<div align="center">\r\n					210<br />\r\n					<br />\r\n					75<br />\r\n					<br />\r\n					320<br />\r\n					<br />\r\n					72<br />\r\n					<br />\r\n					70</div>\r\n			</td>\r\n		</tr>\r\n		<tr bgcolor="#F2F2F2">\r\n			<td>\r\n				&nbsp;</td>\r\n			<td colspan="2">\r\n				<div align="center">\r\n					Микроэлементы ( г/га )</div>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td bgcolor="#F2F2F2">\r\n				<div align="center">\r\n					Fe<br />\r\n					<br />\r\n					Mn<br />\r\n					<br />\r\n					Zn<br />\r\n					<br />\r\n					Cu<br />\r\n					<br />\r\n					В<br />\r\n					<br />\r\n					Mo</div>\r\n			</td>\r\n			<td>\r\n				<div align="center">\r\n					550<br />\r\n					<br />\r\n					400<br />\r\n					<br />\r\n					155<br />\r\n					<br />\r\n					37<br />\r\n					<br />\r\n					28<br />\r\n					<br />\r\n					3,2</div>\r\n			</td>\r\n			<td>\r\n				<div align="center">\r\n					10.000<br />\r\n					<br />\r\n					1.600<br />\r\n					<br />\r\n					750<br />\r\n					<br />\r\n					76<br />\r\n					<br />\r\n					204<br />\r\n					<br />\r\n					7,5</div>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<div align="justify" class="maintext">\r\n	<p>\r\n		<br />\r\n		Микроэлементы (группа тяжелых металлов - Zn, Cu, Mn, Fe) в удобрениях &quot;ЯРА&quot; (Гидро Агри), в отличие от сульфатной формы, имеющей ряд недостатков, представлены в виде хелатов. Это нутрикомплексные металл органические соединения, легко растворимые в воде и доступные растениям. Они практически не закрепляются почвой, как простые соли, и не разрушают органические структуры действующего вещества пестицидов, что делает возможным совмещение обработок.<br />\r\n		<br />\r\n		Нормы внесения удобрений (NPK) рассчитывают, исходя из коэффициента использования растениями удобрения, обеспеченности почвы подвижными формами элементов минерального питания, планируемой урожайности и выноса этих элементов с урожаем.<br />\r\n		<br />\r\n		Известно, что коэффициент использования питательных веществ из минеральных удобрений сельскохозяйственными растениями открытого грунта нельзя назвать высоким. Так, при внесении в почву азот и калий из азотных и калийных удобрений используется растениями на 30-60% (Петербургский, 1979), а фосфор, по разным данным, от 16 до 40% (Кореньков, 1980; Шапошникова, Листопадов, 1984; Емельянов, 1986).<br />\r\n		<br />\r\n		Из большого разнообразия факторов, влияющих на коэффициент использования растениями питательных веществ из удобрений (КИУ), можно выделить основные: вид удобрения, тип почвы, способ внесения удобрений, погодные условия. Однако полный отказ от внесения удобрений под зябь недопустим (можно говорить о снижении доз при дробном внесении), так как запас более стабильных в почве фосфора и калия на глубине пахотного горизонта необходим для поддержания нормального уровня минерального питания растений средних и поздних фаз вегетации.<br />\r\n		<br />\r\n		Дробное внесение минеральных удобрений (основное, предпосевное, припосевное и подкормки) повышает КИУ, а следовательно, и экономическую эффективность агроприема.<br />\r\n		<br />\r\n		В связи с тем что погодно-климатический и почвенный факторы, влияющие на КИУ, в меньшей степени зависимы от человека, выбор вида удобрения, способа и сроков его внесения следует рассматривать как наиболее доступный способ повышения экономической эффективности применения удобрений. Коэффициент использования всего комплекса питательных веществ <strong>Кристалона</strong>, включая микроэлементы (внекорневая подкормка), - 85-90%.<br />\r\n		<em>Минимальный вынос</em> элементов минерального питания с урожаем (полевые культуры) осуществляют зерновые, <em>максимальный</em> - сахарная и кормовая свекла. Получение высокого урожая хорошего качества требует постоянного пополнения запасов макро- и микроэлементов, идущих на его создание.<br />\r\n		<br />\r\n		Прибавка урожайности озимой пшеницы в расчете на 1 кг д.в. удобрения составляет 10-15 кг зерна. Каждый килограмм минеральных удобрении в основных свеклосеющих районах при правильном применении (особенно азотных) дает около 1,5 кг сахара, а в Нечерноземье и на орошении - 2-3 кг (П.П. Вавилов). <strong>Однако затраты на весь комплекс необходимых работ и материалов, при выращивании сельскохозяйственных культур, могут не окупиться в силу возникновения стресс-факторов и нарушения корневого питания. </strong><br />\r\n		<br />\r\n		Применение <strong>Кристалона</strong> по листу в критические моменты снижает или нейтрализует воздействие стресс-факторов, поддерживая и стимулируя питание растений. Это гарантирует эффективность NPK и всей технологии, а также прибавку урожая и его качество. В течение ряда лет применение на больших площадях <strong>Кристалона</strong> на практике показало, что дополнительные затраты на его применение в <em>1 рубль на 1 га</em> оборачиваются прибылью в <em>10 - 30 рублей с 1 га</em> на различных сельскохозяйственых культурах.<br />\r\n		<br />\r\n		Кристалон специальный - сертификат анализа Государственной лаборатории Нидерландов, ТГУ (Торфо-гуминовое удобрение), ЕАП* - протокол №5029 от 29.05.97г. - испытательной лаборатории ФГУ Центра агрохимической службы &quot;Краснодарский&quot;<br />\r\n		<em>* - химическая форма кальция и микроэлементов в ТГУ ЕАП в протоколе испытаний не отражена.</em></p>\r\n</div>\r\n<p>\r\n	Соотношение между уровнем внесения удобрений и прибылью<br />\r\n	<img height="433" src="http://kristalon.ru/pics/pictures/udobraniya-pribil.jpg" width="642" /></p>\r\n', '<div align="justify">\r\n	<p>\r\n		<em>Без организации эффективного минерального питания выращивание сельскохозяйственных культур низкорентабельно, теряют смысл затраты на семена, пестициды и комплекс полевых и уборочных работ.</em><br />\r\n		<br />\r\n		Особое значение, именно в эффективности питания, имеют микроэлементы - бор, молибден, медь, цинк, железо, марганец. Ни одно растение не может нормально развиваться без этих элементов, так как они входят в состав важнейших ферментов, витаминов, гормонов и других физиологически активных соединений, играющих большую роль в жизни растений. Микроэлементы участвуют в процессах синтеза белков, углеводов, жиров, витаминов. Под их влиянием увеличивается содержание хлорофилла в листьях, усиливается ассимилирующая деятельность всего растения, улучшается процесс фотосинтеза. Исключительно важную роль играют микроэлементы в процессах оплодотворения. Они положительно влияют на развитие семян и их посевные качества. Под их воздействием растения становятся более устойчивыми к неблагоприятным условиям выращивания, к засухе, поражению болезнями, вредителями и т.д.<br />\r\n		<br />\r\n		Компания &laquo;ЯРА&raquo; (Гидро Агри) - одно из подразделений Норвежского государственного концерна Норск Гидро - около ста лет назад первой в мире начала промышленное производство минеральных удобрений. Это лидерство сохранено до настоящего времени как в качестве, так и в количестве (12-15 млн. тонн в год). Достижения мировой агрохимии и столетний опыт воплотились в поистине уникальные формы и виды (около 600) минеральных удобрений для различных культур и условий выращивания, имеющие в своем составе все необходимые и легко доступные растениям макро- и микроэлементы (в хелатной форме) в сбалансированном соотношении. Особые формулы минеральных удобрений &laquo;ЯРА&raquo; (Гидро), химическая чистота, отсутствие вредных соединений, удобная расфасовка и Доступные цены обуславливают широкий спектр их применения - от комнатных Растений до полевых культур и декоративных насаждений.<br />\r\n		<br />\r\n		Использование удобрений &laquo;ЯРА&raquo; (Гидро Агри) по специально разработанным технологиям (для любых сельскохозяйственных культур) позволяют получать максимальный эффект при незначительных затратах. Растения находятся в многосторонней и тесной связи с окружающей внешней средой. При благоприятном сочетании всех факторов жизни получают максимальную продуктивность растений и качество урожая. Недостаток одного из условий жизни растения угнетает его развитие, а отсутствие приводит к гибели. В практике земледелия чаще приходится сталкиваться с недостатком питательных веществ и воды.<br />\r\n		<br />\r\n		Практически любой стресс-фактор приводит к нарушению питания растений - засуха, низкая или высокая температура и влажность почвы или воздуха, уплотненность почвы и плохая аэрация, низкий или высокий показатель pH, высокое содержание ионов антагонистов и органических веществ. То есть даже при достаточном количестве элементов питания в почве растения не всегда в состоянии использовать их в полной мере. Нарушение питания - это прямые потери урожая и качества.</p>\r\n</div>\r\n<div align="center" class="maintext-table-head">\r\n	Основное влияние элементов минерального питания на процессы,<br />\r\n	определяющие урожаи и его качество.<br />\r\n	<br />\r\n	Макроэлементы<br />\r\n	(их вынос с урожаем исчисляется в кг на 1 т продукции)</div>\r\n\r\n<table align="center" border="1" cellpadding="0" cellspacing="0" class="maintext-table-text" width="98%">\r\n	<tbody>\r\n		<tr bgcolor="#E1E1E1" valign="top">\r\n			<td bgcolor="#E1E1E1" valign="top" width="98">\r\n				<div align="center">\r\n					<strong>Азот</strong></div>\r\n			</td>\r\n			<td valign="top" width="136">\r\n				<div align="center">\r\n					<strong>Фосфор</strong></div>\r\n			</td>\r\n			<td valign="top" width="134">\r\n				<div align="center">\r\n					<strong>Калий</strong></div>\r\n			</td>\r\n			<td valign="top" width="111">\r\n				<div align="center">\r\n					<strong>Магний</strong></div>\r\n			</td>\r\n			<td valign="top" width="97">\r\n				<div align="center">\r\n					<strong>Кальций</strong></div>\r\n			</td>\r\n		</tr>\r\n		<tr valign="top">\r\n			<td bgcolor="#F2F2F2" valign="top" width="20%">\r\n				Элемент образования органического вещества.<br />\r\n				Регулирует рост вегетативной массы. Определяет уровень урожайности</td>\r\n			<td valign="top" width="20%">\r\n				Элемент энергетического обеспечения. Активирует рост корневой системы и закладки генеративных органов. Ускоряет развитие всех процессов. Повышает зимостойкость</td>\r\n			<td bgcolor="#F2F2F2" valign="top" width="20%">\r\n				Элемент молодости клеток. Усиливает образование сахаров и их передвижение по тканям. Повышает устойчивость к болезням, засухе и заморозкам.</td>\r\n			<td valign="top" width="20%">\r\n				Повышает интенсивность фотосинтеза и образование хлорофилла. Влияет на окислительно-восстановительные процессы. Активирует ферментативные про цессы.</td>\r\n			<td bgcolor="#F2F2F2" valign="top" width="20%">\r\n				Стимулирует рост растения и развитие корневой системы. Усиливает обмен веществ, активирует ферменты.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>');
INSERT INTO `fertilizers_products` (`id`, `public`, `images`, `rutitle`, `rotitle`, `rudesc`, `rodesc`, `mneto`, `price`, `aaa`) VALUES
(1, 1, 'http://kristalon.ru/pics/production/kristalon-specialniy-thumb.jpg', 'Кристалон специальный', 'Кристалон специальный', 'Зерновые,\r\nкукуруза, рис', 'Зерновые,\r\nкукуруза, рис', '25кг ', '25гр', ''),
(2, 1, 'http://kristalon.ru/pics/production/kristalon-jeltiy-thumb.jpg', 'Кристалон желтый', 'Кристалон желтый', 'Подсолнечник', 'Подсолнечник', '25 кг', '25 гр', ''),
(3, 1, 'http://kristalon.ru/pics/production/kristalon-korichneviy-thumb.jpg', 'Кристалон коричневый', 'Кристалон коричневый', 'Виноград, зеленый\r\nгорошек, сахарная\r\nсвекла', 'Виноград, зеленый\r\nгорошек, сахарная\r\nсвекла', '25 кг', '25 гр', ''),
