(9, 1, '/skins/images/templates/noimage.jpg', 'Рефрактометр', 'Рефрактометр', ' 	\r\nПрибор для определения сахарисности с\\х культур ', ' 	\r\nПрибор для определения сахарисности с\\х культур ', '', '250 гр', '');
