INSERT INTO `pages` (`id`, `public`, `page`, `images`, `rutitle`, `rotitle`, `rudesc`, `rodesc`) VALUES
(148, 1, 'about', '', 'О нас', 'About us', '<p>\r\n	<span style="font-family: arial, sans-serif; font-size: 14px;">Официальный дистрибьютор компаний: </span></p>\r\n<p>\r\n	<span style="font-family: arial, sans-serif; font-size: 14px;">Seminis, DE RUITER SEEDS - семена овощей, </span></p>\r\n<p>\r\n	<span style="font-family: arial, sans-serif; font-size: 14px;">Lima - комплексные водорастворимые удобрения, </span></p>\r\n<p>\r\n	<span style="font-family: arial, sans-serif; font-size: 14px;">A.A.Politiv - пленка тепличная и мульчирующая профессиональная, </span></p>\r\n<p>\r\n	<span style="font-family: arial, sans-serif; font-size: 14px;">LUTRASIL - агроволокно, </span></p>\r\n<p>\r\n	<span style="font-family: arial, sans-serif; font-size: 14px;">Sulunkiai - трофяной субстрат, </span></p>\r\n<p>\r\n	<span style="font-family: arial, sans-serif; font-size: 14px;">Terplast - шпагат полипропиленовый, </span></p>\r\n<p>\r\n	<span style="font-family: arial, sans-serif; font-size: 14px;">Intermas- различные виды пластиковых сеток, </span></p>\r\n<p>\r\n	<span style="font-family: arial, sans-serif; font-size: 14px;">Herkuplast - кассеты рассадные, пестициды, капельное орошение и др.&nbsp;</span></p>\r\n<p>\r\n	&nbsp;</p>\r\n', 'Lorem ipsum dolor sit amet, consec tetuer adipi scing elit. Praesent vestibulum molestie lacus. Aenean nonummy hen drerit mauris. Phasellus porta. Fusce suscipit varius mi...');
INSERT INTO `peat` (`id`, `public`, `cat`, `image`, `rutitle`, `rotitle`, `rudesc`, `rodesc`) VALUES
(1, 1, '1', 'http://sulinkiai.lt/files/big_images/prd_01-17_big.gif', 'BIO seeding&herbs soil', 'BIO seeding&herbs soil', 'The only source of nutrients in bio seeding&herbs soil are natural and organic materials authorized for the organic horticulture (market gardening, floriculture, arboriculture, nurseries) according to EC Regulation No. 889/2008.\r\nSuliflor BIO seeding soil is suitable for a wide range of growing needs. It has fine structure, recommended for seed germination, for growing herbs in containers - Basil, Parsley, Chives, Rosemary, Sage, French Tarragon, Thyme, for bedding and vegetable plant crops.\r\nOrganic long lasting bio-fertilizer supplies plants with all natural nutrients up to 75-100 days. Improves rooting, flowering, fruit formation, brings microbiological activity into soil.\r\nPeat - makes soil very porous. Free of weeds, diseases and impurities.\r\nNatural limestone. Adjusts pH of the soil, supplies essential plant nutrients Ca + Mg.\r\nSafety information. Substrates have no harmful effect on people, animals and environment when used in accordance to the instructions for use. Storage time-unlimited.\r\n \r\nProduct declaration\r\n \r\nType of product\r\n	\r\nPeat based growing media\r\nTrade name\r\n	\r\nSuliFlor BIO seeding&herbs soil\r\nQuantity (EN 12580) when filled\r\n	\r\n20L\r\nInstructions for use\r\n	\r\nPropagation soil, fine structured. For sowing, sticking, pricking out for young vegetables, flowers etc.\r\nRaw material and basic components\r\n	\r\nPeat moss H5-H8, all nutrients required for healthy plant growth\r\nStructure\r\n	\r\nFine\r\nOrganic matter\r\n	\r\n> 80%\r\npH (EN 13037)\r\n	\r\n5.5 - 6.5\r\nEC mS/cm (1:1.5)\r\n	\r\n0.5 - 1.5\r\nCountry of Origin\r\n	\r\nLithuania, www.sulinkiai.lt', 'The only source of nutrients in bio seeding&herbs soil are natural and organic materials authorized for the organic horticulture (market gardening, floriculture, arboriculture, nurseries) according to EC Regulation No. 889/2008.\r\nSuliflor BIO seeding soil is suitable for a wide range of growing needs. It has fine structure, recommended for seed germination, for growing herbs in containers - Basil, Parsley, Chives, Rosemary, Sage, French Tarragon, Thyme, for bedding and vegetable plant crops.\r\nOrganic long lasting bio-fertilizer supplies plants with all natural nutrients up to 75-100 days. Improves rooting, flowering, fruit formation, brings microbiological activity into soil.\r\nPeat - makes soil very porous. Free of weeds, diseases and impurities.\r\nNatural limestone. Adjusts pH of the soil, supplies essential plant nutrients Ca + Mg.\r\nSafety information. Substrates have no harmful effect on people, animals and environment when used in accordance to the instructions for use. Storage time-unlimited.\r\n \r\nProduct declaration\r\n \r\nType of product\r\n	\r\nPeat based growing media\r\nTrade name\r\n	\r\nSuliFlor BIO seeding&herbs soil\r\nQuantity (EN 12580) when filled\r\n	\r\n20L\r\nInstructions for use\r\n	\r\nPropagation soil, fine structured. For sowing, sticking, pricking out for young vegetables, flowers etc.\r\nRaw material and basic components\r\n	\r\nPeat moss H5-H8, all nutrients required for healthy plant growth\r\nStructure\r\n	\r\nFine\r\nOrganic matter\r\n	\r\n> 80%\r\npH (EN 13037)\r\n	\r\n5.5 - 6.5\r\nEC mS/cm (1:1.5)\r\n	\r\n0.5 - 1.5\r\nCountry of Origin\r\n	\r\nLithuania, www.sulinkiai.lt');
INSERT INTO `pellicle` (`id`, `public`, `rutitle`, `rotitle`, `rucontent`, `rocontent`) VALUES
(1, 1, 'Плёнка тепличная производство Израиль.', 'Плёнка тепличная производство Израиль.', '1.Многослойный состав\r\n1. Супер долговечный наружный слой противостоит экстремальному УФ облучению и защищает растения из года в год. Произведён из первоклассных ко-полимеров на совершенном компьютеризированном оборудовании. Противостоит загрязнению и росту водорослей, содержит присадки для облегчения мытья кровли. \r\n2. Второй слой состоит из полиэтилена низкого давления, что придаёт плёнке чрезвычайную прочность на растяжение и разрыв. Хорошо противостоит ветровым нагрузкам. Этот слой придаёт плёнке долговечность на точках соприкосновения с рамой теплицы. \r\n3. Внутренний слой произведён из таких же материалов, как и наружный слой, кроме того, содержит анти-конденсатное покрытие. Этот слой влияет на урожайность и предотвращает возникновение капель на поверхности плёнки, а возникающий конденсат стекает, скользит к краям теплицы без капели на растения.\r\nТ.о.  растения остаются сухими, что ведёт к снижению роста грибных и прочих заболеваний, быстро развивающихся во влажных условиях.\r\nОтсутствие капель на поверхности также значительно улучшает светопроницаемость плёнки, что особенно важно в зимний период,- это сокращает энергопотери из теплицы в ночной период. \r\n', '1.Многослойный состав\r\n1. Супер долговечный наружный слой противостоит экстремальному УФ облучению и защищает растения из года в год. Произведён из первоклассных ко-полимеров на совершенном компьютеризированном оборудовании. Противостоит загрязнению и росту водорослей, содержит присадки для облегчения мытья кровли. \r\n2. Второй слой состоит из полиэтилена низкого давления, что придаёт плёнке чрезвычайную прочность на растяжение и разрыв. Хорошо противостоит ветровым нагрузкам. Этот слой придаёт плёнке долговечность на точках соприкосновения с рамой теплицы. \r\n3. Внутренний слой произведён из таких же материалов, как и наружный слой, кроме того, содержит анти-конденсатное покрытие. Этот слой влияет на урожайность и предотвращает возникновение капель на поверхности плёнки, а возникающий конденсат стекает, скользит к краям теплицы без капели на растения.\r\nТ.о.  растения остаются сухими, что ведёт к снижению роста грибных и прочих заболеваний, быстро развивающихся во влажных условиях.\r\nОтсутствие капель на поверхности также значительно улучшает светопроницаемость плёнки, что особенно важно в зимний период,- это сокращает энергопотери из теплицы в ночной период. \r\n'),
(2, 1, 'dsadas', 'sadasdasda', '', 'dsadsadsadasda');
